package com.example.scanner

import com.example.scanner.features.api.Gif
import com.example.scanner.features.api.GiphyApi
import com.example.scanner.features.api.VisionApi
import com.example.scanner.features.gifList.GifListUiState
import com.example.scanner.features.gifList.GifListViewModel
import com.nhaarman.mockitokotlin2.any
import com.nhaarman.mockitokotlin2.whenever
import org.junit.Assert
import org.junit.Test
import org.mockito.Mockito.mock
import retrofit2.Call
import retrofit2.mock.Calls
import java.io.IOException

val fakeGif = Gif(
    id = "123",
    title = "Eren",
    url = "https://i.redd.it/vjtnz6zrd79f1.gif",
    importDatetime = "2025-08-07 01:56:11",
    updatedAt = 1700000000000L
)

class GifViewModelTest {

    @Test
    fun `api fail produces a Failure state`() {
        // Arrange
        val viewModel = GifListViewModel()
        val mockApi = mock<VisionApi>()

        whenever(mockApi.postVision(any(), any()))
            .thenReturn(Calls.failure(IOException()))
        viewModel.apiVision = mockApi

        // Act
        viewModel.testVision("")

        // Assert
        Assert.assertEquals(
            GifListUiState.Failure("error"),
            viewModel.uiState.value
        )
    }
}